        static void printMaze(char[,] maze)
        {
            for (int row = 0; row < maze.GetLength(0); row++)
            {
                for (int col = 0; col < maze.GetLength(1); col++)
                {
                    Console.Write(maze[row, col]);
                }
                Console.WriteLine();
            }
        }
        static void printHead()
        {
            Console.Clear();
            gameTitle();
        }
        static void menu()
        {
            Console.ForegroundColor = ConsoleColor.Yellow;
            Console.WriteLine("Main Menu");
            Console.WriteLine("-------------------------------------");
            Console.ForegroundColor = ConsoleColor.Red;
            Console.WriteLine("1- Start");
            Console.WriteLine("2- Option");
            Console.WriteLine("3- Exit");
            Console.ForegroundColor = ConsoleColor.White;
        }
        static void loadMenu()
        {
            Console.ForegroundColor = ConsoleColor.Yellow;
            Console.WriteLine("Menu");
            Console.WriteLine("-------------------------------------");
            Console.ForegroundColor = ConsoleColor.Red;
            Console.WriteLine("1- Start New Game");
            Console.WriteLine("2- Load Game");
            Console.ForegroundColor = ConsoleColor.White;
        }
        static void subMenu(string subMenu)
        {
            Console.ForegroundColor = ConsoleColor.Yellow;
            string message = subMenu;
            Console.WriteLine(message);
            Console.WriteLine("-------------------------------------");
            Console.ForegroundColor = ConsoleColor.White;
        }
        static string choice()
        {
            Console.ForegroundColor = ConsoleColor.Yellow;
            string option;
            Console.Write("Enter Your Choice: ");
            option = Console.ReadLine();
            Console.ForegroundColor = ConsoleColor.White;
            return option;
        }
        static void clear()
        {
            Console.ForegroundColor = ConsoleColor.Yellow;
            Console.WriteLine("Press any key to continue ");
            Console.ReadKey();
            Console.ForegroundColor = ConsoleColor.White;
        }
        static void options()
        {
            Console.ForegroundColor = ConsoleColor.Yellow;
            Console.WriteLine("Select one of the following option...");
            Console.WriteLine("-------------------------------------");
            Console.ForegroundColor = ConsoleColor.Red;
            Console.WriteLine("1- Keys");
            Console.WriteLine("2- Instructions");
            Console.WriteLine("3- Back");
            Console.ForegroundColor = ConsoleColor.White;
        }
        static void keys()
        {
            Console.ForegroundColor = ConsoleColor.Red;
            Console.WriteLine("1. UP         JUMP");
            Console.WriteLine("2. LEFT       GO LEFT");
            Console.WriteLine("3. RIGHT      GO RIGHT");
            Console.WriteLine("4. SPACE      FIRE");
            Console.ForegroundColor = ConsoleColor.White;
        }
        static void instructions()
        {
            Console.ForegroundColor = ConsoleColor.Red;
            Console.WriteLine("1- Food Pallet will increase the score.");
            Console.WriteLine("2- Player bullet hits the enemies will also increase the score.");
            Console.WriteLine("3- Enemies Bullet decrease the player health.");
            Console.WriteLine("4- Health Pill will gives a player life.");
            Console.WriteLine("5- Collect the three keys that will open the door.");
            Console.ForegroundColor = ConsoleColor.White;
        }
