        // Score
        static void printScore(int score)
        {
            Console.SetCursorPosition(110, 2);
            Console.WriteLine("Score: " + score);
        }
        static void addScore(ref int score)
        {
            score = score + 2;
        }

        // Bullet Collisions
        static void bulletCollisionWithEnemy(List<Fireboy> p, Enemy1 icy, char[,] maze, ref int score, ref int enemy1Health, char[,] enemy1, ref bool enemy1Present)
        {
            for (int x = 0; x < p.Count(); x++)
            {
                if (p[x].bulletY + 1 == icy.enemy1Y && (p[x].bulletX == icy.enemy1X || p[x].bulletX == icy.enemy1X + 1 || p[x].bulletX == icy.enemy1X + 2))
                {
                    addScore(ref score);
                    printScore(score);
                    enemy1Health = enemy1Health - 10;
                    icy.enemy1_health(maze, enemy1, ref enemy1Present, ref enemy1Health);
                }
                if (icy.enemy1X - 1 == p[x].bulletX && icy.enemy1Y + 1 == p[x].bulletY)
                {
                    addScore(ref score);
                    printScore(score);
                    enemy1Health = enemy1Health - 10;
                    icy.enemy1_health(maze, enemy1, ref enemy1Present, ref enemy1Health);
                }
            }
            /*
            if (bulletX[x] + 1 == enemy2X && (bulletY[x] == enemy2Y || bulletY[x] == enemy2Y + 1 || bulletY[x] == enemy2Y + 2 || bulletY[x] == enemy2Y + 3 || bulletY[x] == enemy2Y + 4))
            {
                addScore();
                printScore();
                enemy2Health = enemy2Health - 10;
                enemy2_health();
            }
            if (bulletX[x] + 1 == enemy3X && (bulletY[x] == enemy3Y || bulletY[x] == enemy3Y + 1 || bulletY[x] == enemy3Y + 2))
            {
                addScore();
                printScore();
                enemy3Health = enemy3Health - 10;
                enemy3_health();
            }
