        }
        static void Enemy1BulletCollisionWithPlayer(List<Enemy1> e, Fireboy p, ref int fireBoyHealth, ref int healthCount)
        {
            for (int x = 0; x < e.Count(); x++)
            {
                if (e[x].bulletEnemy1X == p.fireBoyX && e[x].bulletEnemy1Y - 1 == p.fireBoyY + 1)
                {
                    fireBoyHealth = fireBoyHealth - 10;
                    p.playerHealth(ref fireBoyHealth, ref healthCount);
                }
                if (e[x].bulletEnemy1X == p.fireBoyX + 1 && e[x].bulletEnemy1Y - 1 == p.fireBoyY + 2)
                {
                    fireBoyHealth = fireBoyHealth - 10;
                    p.playerHealth(ref fireBoyHealth, ref healthCount);
                }
                if (e[x].bulletEnemy1X == p.fireBoyX + 2 && e[x].bulletEnemy1Y - 1 == p.fireBoyY + 1)
                {
                    fireBoyHealth = fireBoyHealth - 10;
                    p.playerHealth(ref fireBoyHealth, ref healthCount);
                }
                if (e[x].bulletEnemy1X + 1 == p.fireBoyX && e[x].bulletEnemy1Y == p.fireBoyY)
                {
                    fireBoyHealth = fireBoyHealth - 10;
                    p.playerHealth(ref fireBoyHealth, ref healthCount);
                }
                if (e[x].bulletEnemy1X + 1 == p.fireBoyX && e[x].bulletEnemy1Y == p.fireBoyY + 1)
                {
                    fireBoyHealth = fireBoyHealth - 10;
                    p.playerHealth(ref fireBoyHealth, ref healthCount);
                }
                if (e[x].bulletEnemy1X + 1 == p.fireBoyX + 2 && e[x].bulletEnemy1Y == p.fireBoyY + 2)
                {
                    fireBoyHealth = fireBoyHealth - 10;
                    p.playerHealth(ref fireBoyHealth, ref healthCount);
                }
                if (e[x].bulletEnemy1X - 1 == p.fireBoyX && e[x].bulletEnemy1Y == p.fireBoyY)
                {
                    fireBoyHealth = fireBoyHealth - 10;
                    p.playerHealth(ref fireBoyHealth, ref healthCount);
                }
                if (e[x].bulletEnemy1X - 1 == p.fireBoyX + 1 && e[x].bulletEnemy1Y == p.fireBoyY)
                {
                    fireBoyHealth = fireBoyHealth - 10;
                    p.playerHealth(ref fireBoyHealth, ref healthCount);
                }
                if (e[x].bulletEnemy1X - 1 == p.fireBoyX + 2 && e[x].bulletEnemy1Y == p.fireBoyY)
                {
                    fireBoyHealth = fireBoyHealth - 10;
                    p.playerHealth(ref fireBoyHealth, ref healthCount);
                }
            }
        }
